package org.tiktzuki.leetcode.medium;

public class LargestPalindromicNumber {
    public String largestPalindromic(String num) {
        int[] counter = new int[10];
        for (char c : num.toCharArray()) {
            counter[c - '0']++;
        }
        StringBuilder sb = new StringBuilder();
        int midNumber = -1;
        for (int i = 9; i >= 0; i--) {
            if (sb.length()==0 && i == 0 && midNumber == -1)
                return "0";
            int numberCount = counter[i];
            if (numberCount % 2 == 1) {
                if (midNumber == -1)
                    midNumber = i;
                numberCount--;
            }
            while (numberCount > 0 && numberCount % 2 == 0) {
                numberCount -= 2;
                if (sb.length()==0 && i == 0)
                    continue;
                sb.insert(sb.length() / 2, i + "" + i);
            }
        }
        if (midNumber != -1) {
            sb.insert(sb.length() / 2, midNumber);
        }
        return sb.toString();
    }

    public static void main(String[] args) {
        var solution = new LargestPalindromicNumber();
        for (String s : new String[]{
//                "444947137",
//                "998877",
//                "54321",
//                "0000000",
//                "00001105",
                "00009",
                ""
        }) {
            System.out.println(solution.largestPalindromic(s));
        }
    }
}
